import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { useProdutos } from '../context/ProdutosContext';

function DetalhesProduto() {
  const { id } = useParams();
  const { produtos } = useProdutos();

  const produto = produtos.find(p => p.id === parseInt(id));

  if (!produto) {
    return (
      <div className="container">
        <div style={{ marginBottom: '20px' }}>
          <Link to="/home" style={{ color: '#ff69b4', textDecoration: 'none' }}>‚Üê Voltar ao In√≠cio</Link>
        </div>
        <h2 style={{ textAlign: 'center', color: '#ff69b4' }}>Produto n√£o encontrado</h2>
      </div>
    );
  }

  const handleWhatsApp = () => {
    const mensagem = `Ol√°! Tenho interesse no produto: ${produto.nome}`;
    const numero = produto.contato.replace(/\D/g, '');
    window.open(`https://wa.me/55${numero}?text=${encodeURIComponent(mensagem)}`, '_blank');
  };

  return (
    <div className="container">
      <div style={{ marginBottom: '20px' }}>
        <Link to="/home" style={{ color: '#ff69b4', textDecoration: 'none' }}>‚Üê Voltar ao In√≠cio</Link>
      </div>

      <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h2 style={{ color: '#ff69b4', marginBottom: '20px' }}>{produto.nome}</h2>

        <div style={{ marginBottom: '15px' }}>
          <strong>Categoria:</strong> {produto.categoria}
        </div>

        <div style={{ marginBottom: '15px' }}>
          <strong>Estado:</strong> {produto.estado}
        </div>

        <div style={{ marginBottom: '15px' }}>
          <strong>Descri√ß√£o:</strong>
          <p style={{ marginTop: '5px', lineHeight: '1.5' }}>{produto.descricao}</p>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <strong>Doador:</strong> {produto.doador}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <strong>Contato:</strong> {produto.contato}
        </div>

        <button 
          onClick={handleWhatsApp}
          style={{ 
            width: '100%',
            padding: '12px',
            backgroundColor: '#25D366',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            fontSize: '16px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          üí¨ Entrar em Contato via WhatsApp
        </button>
      </div>
    </div>
  );
}

export default DetalhesProduto;
