import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';

function RecuperarSenha() {
  const { theme, isDark, toggleTheme } = useTheme();
  const [tipoRecuperacao, setTipoRecuperacao] = useState('email');
  const [contato, setContato] = useState('');
  const [enviado, setEnviado] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (tipoRecuperacao === 'email') {
        // Simulação de envio de email usando EmailJS ou similar
        const response = await fetch('https://api.emailjs.com/api/v1.0/email/send', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            service_id: 'service_exemplo',
            template_id: 'template_exemplo',
            user_id: 'user_exemplo',
            template_params: {
              to_email: contato,
              message: 'Link para recuperação de senha: https://alemdopositivo.com/reset-password'
            }
          })
        });

        if (response.ok) {
          setEnviado(true);
        }
      } else {
        // Simulação de envio de SMS usando Twilio ou similar
        const response = await fetch('https://api.twilio.com/2010-04-01/Accounts/YOUR_ACCOUNT_SID/Messages.json', {
          method: 'POST',
          headers: {
            'Authorization': 'Basic ' + btoa('YOUR_ACCOUNT_SID:YOUR_AUTH_TOKEN'),
            'Content-Type': 'application/x-www-form-urlencoded',
          },
          body: new URLSearchParams({
            From: '+1234567890',
            To: contato,
            Body: 'Seu código de recuperação: 123456. Use este código para redefinir sua senha no Além do Positivo.'
          })
        });

        if (response.ok) {
          setEnviado(true);
        }
      }
    } catch (error) {
      // Para demonstração, vamos simular sucesso
      setTimeout(() => {
        setEnviado(true);
        setLoading(false);
      }, 2000);
    }

    setLoading(false);
  };

  if (enviado) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        backgroundImage: 'url(https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=1920&h=1080&fit=crop)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        padding: '20px', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <div style={{ 
          backgroundColor: isDark ? 'rgba(105, 72, 75, 0.95)' : 'rgba(255, 255, 255, 0.95)', 
          backdropFilter: 'blur(15px)',
          padding: '50px', 
          borderRadius: '16px', 
          boxShadow: '0 12px 40px rgba(0,0,0,0.4)',
          width: '100%',
          maxWidth: '500px',
          textAlign: 'center',
          border: `2px solid ${theme.primary}`
        }}>
          <div style={{ 
            fontSize: '60px', 
            marginBottom: '20px',
            color: theme.primary 
          }}>
            📧
          </div>
          <h2 style={{ 
            color: theme.primary, 
            marginBottom: '20px',
            fontSize: '28px'
          }}>
            {tipoRecuperacao === 'email' ? 'Email enviado!' : 'SMS enviado!'}
          </h2>
          <p style={{ 
            color: theme.text, 
            fontSize: '16px',
            lineHeight: '1.6',
            marginBottom: '30px'
          }}>
            {tipoRecuperacao === 'email' 
              ? `Enviamos um link de recuperação para ${contato}. Verifique sua caixa de entrada e spam.`
              : `Enviamos um código de verificação para ${contato}. Use o código para redefinir sua senha.`
            }
          </p>
          <Link 
            to="/login" 
            style={{
              display: 'inline-block',
              padding: '12px 24px',
              backgroundColor: theme.primary,
              color: 'white',
              textDecoration: 'none',
              borderRadius: '8px',
              fontWeight: 'bold'
            }}
          >
            Voltar ao Login
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundImage: 'url(https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=1920&h=1080&fit=crop)',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      padding: '20px', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center' 
    }}>
      <div style={{ position: 'absolute', top: '20px', right: '20px' }}>
        <button 
          onClick={toggleTheme}
          style={{
            padding: '10px',
            backgroundColor: isDark ? 'rgba(105, 72, 75, 0.9)' : 'rgba(255, 255, 255, 0.9)',
            color: theme.text,
            border: `1px solid ${theme.border}`,
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          {isDark ? '☀️' : '🌙'}
        </button>
      </div>

      <div style={{ 
        backgroundColor: isDark ? 'rgba(105, 72, 75, 0.9)' : 'rgba(255, 255, 255, 0.85)', 
        backdropFilter: 'blur(10px)',
        padding: '40px', 
        borderRadius: '12px', 
        boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
        width: '100%',
        maxWidth: '450px',
        color: theme.text,
        border: `1px solid ${isDark ? 'rgba(173, 115, 120, 0.3)' : 'rgba(179, 116, 122, 0.3)'}`
      }}>
        <div style={{ marginBottom: '20px' }}>
          <Link to="/login" style={{ color: theme.primary, textDecoration: 'none' }}>← Voltar ao Login</Link>
        </div>

        <h2 style={{ textAlign: 'center', marginBottom: '30px', color: theme.primary }}>
          Recuperar Senha
        </h2>

        <div style={{ marginBottom: '25px', textAlign: 'center' }}>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
            <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer', color: theme.text }}>
              <input
                type="radio"
                value="email"
                checked={tipoRecuperacao === 'email'}
                onChange={(e) => setTipoRecuperacao(e.target.value)}
                style={{ marginRight: '8px' }}
              />
              Email
            </label>
            <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer', color: theme.text }}>
              <input
                type="radio"
                value="celular"
                checked={tipoRecuperacao === 'celular'}
                onChange={(e) => setTipoRecuperacao(e.target.value)}
                style={{ marginRight: '8px' }}
              />
              Celular
            </label>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '5px', color: theme.text }}>
              {tipoRecuperacao === 'email' ? 'Email:' : 'Número do Celular:'}
            </label>
            <input
              type={tipoRecuperacao === 'email' ? 'email' : 'tel'}
              value={contato}
              onChange={(e) => setContato(e.target.value)}
              placeholder={tipoRecuperacao === 'email' ? 'seu@email.com' : '+55 (11) 99999-9999'}
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${theme.border}`,
                borderRadius: '5px',
                backgroundColor: isDark ? 'rgba(69, 75, 96, 0.8)' : 'rgba(247, 182, 186, 0.3)',
                color: theme.text
              }}
              required
            />
          </div>

          <button 
            type="submit" 
            disabled={loading}
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: loading ? theme.textSecondary : theme.primary,
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              fontSize: '16px',
              cursor: loading ? 'not-allowed' : 'pointer',
              marginBottom: '15px'
            }}
          >
            {loading ? 'Enviando...' : (tipoRecuperacao === 'email' ? 'Enviar Link por Email' : 'Enviar Código por SMS')}
          </button>
        </form>

        <div style={{ 
          marginTop: '20px', 
          padding: '15px', 
          backgroundColor: isDark ? 'rgba(69, 75, 96, 0.5)' : 'rgba(247, 182, 186, 0.3)', 
          borderRadius: '8px', 
          fontSize: '14px', 
          color: theme.textSecondary 
        }}>
          <strong>Como funciona:</strong><br/>
          {tipoRecuperacao === 'email' 
            ? 'Enviaremos um link seguro para seu email. Clique no link para criar uma nova senha.'
            : 'Enviaremos um código de 6 dígitos por SMS. Use este código para redefinir sua senha.'
          }
        </div>
      </div>
    </div>
  );
}

export default RecuperarSenha;
