import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';

function Login({ setUser }) {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [tipoLogin, setTipoLogin] = useState('usuario');
  const [showEsqueciSenha, setShowEsqueciSenha] = useState(false);
  const navigate = useNavigate();
  const { theme, isDark, toggleTheme } = useTheme();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (email && senha) {
      if (tipoLogin === 'admin') {
        if (email === 'admin@alemdopositivo.com' && senha === 'admin123') {
          setUser({ email, nome: 'Administrador', isAdmin: true });
          navigate('/admin');
        } else {
          alert('Credenciais de administrador inválidas!');
        }
      } else {
        try {
          const response = await fetch('http://localhost:5000/api/auth/login', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, senha }),
          });

          if (response.ok) {
            const data = await response.json();
            localStorage.setItem('token', data.token);
            setUser({ 
              id: data.id,
              email: data.email, 
              nome: data.nome, 
              isAdmin: data.isAdmin 
            });
            navigate('/home');
          } else {
            const errorData = await response.text();
            alert(errorData || 'Erro no login');
          }
        } catch (error) {
          alert('Erro de conexão com o servidor');
        }
      }
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #ffc0cb 0%, #f8d7da 100%)',
      padding: '20px', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <img 
        src="https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80"
        alt="Mulher grávida"
        style={{
          position: 'absolute',
          left: '10%',
          top: '50%',
          transform: 'translateY(-50%)',
          width: '200px',
          height: '300px',
          objectFit: 'cover',
          borderRadius: '15px',
          opacity: '0.3',
          animation: 'float 3s ease-in-out infinite',
          zIndex: 1
        }}
      />
      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translateY(-50%) translateX(0px); }
            50% { transform: translateY(-50%) translateX(10px); }
          }
        `}
      </style>
      <div style={{ position: 'absolute', top: '20px', right: '20px' }}>
        <button 
          onClick={toggleTheme}
          style={{
            padding: '10px',
            backgroundColor: theme.cardBackground,
            color: theme.text,
            border: `1px solid ${theme.border}`,
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          {isDark ? '☀️' : '🌙'}
        </button>
      </div>
      
      <div style={{ 
        backgroundColor: isDark ? 'rgba(105, 72, 75, 0.9)' : 'rgba(255, 255, 255, 0.85)', 
        backdropFilter: 'blur(10px)',
        padding: '40px', 
        borderRadius: '12px', 
        boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
        width: '100%',
        maxWidth: '400px',
        color: theme.text,
        border: `1px solid ${isDark ? 'rgba(173, 115, 120, 0.3)' : 'rgba(179, 116, 122, 0.3)'}`
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '30px', color: theme.primary }}>
          Login - Além do Positivo
        </h2>
        
        <div style={{ marginBottom: '25px', textAlign: 'center' }}>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
            <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer', color: theme.text }}>
              <input
                type="radio"
                value="usuario"
                checked={tipoLogin === 'usuario'}
                onChange={(e) => setTipoLogin(e.target.value)}
                style={{ marginRight: '8px' }}
              />
              Usuário
            </label>
            <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer', color: theme.text }}>
              <input
                type="radio"
                value="admin"
                checked={tipoLogin === 'admin'}
                onChange={(e) => setTipoLogin(e.target.value)}
                style={{ marginRight: '8px' }}
              />
              Administrador
            </label>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '5px', color: theme.text }}>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder={tipoLogin === 'admin' ? 'Email do administrador' : 'seu@email.com'}
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${theme.border}`,
                borderRadius: '5px',
                backgroundColor: theme.background,
                color: theme.text
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '5px', color: theme.text }}>Senha:</label>
            <input
              type="password"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              placeholder={tipoLogin === 'admin' ? 'Senha do administrador' : 'sua senha'}
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${theme.border}`,
                borderRadius: '5px',
                backgroundColor: theme.background,
                color: theme.text
              }}
              required
            />
          </div>
          <button 
            type="submit" 
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: theme.primary,
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              fontSize: '16px',
              cursor: 'pointer',
              marginBottom: '15px'
            }}
          >
            {tipoLogin === 'admin' ? 'Entrar como Admin' : 'Entrar'}
          </button>
        </form>
        
        {tipoLogin === 'usuario' && (
          <div style={{ textAlign: 'center' }}>
            <Link to="/cadastro" style={{ color: theme.primary, textDecoration: 'none', marginBottom: '10px', display: 'block' }}>
              Não tem conta? Cadastre-se
            </Link>
            <Link 
              to="/recuperar-senha"
              style={{
                color: theme.primary,
                textDecoration: 'underline',
                fontSize: '14px'
              }}
            >
              Esqueci minha senha
            </Link>
          </div>
        )}
      </div>
    </div>
  );
}

export default Login;
