import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useProdutos } from '../context/ProdutosContext';

function Doacao() {
  const [produto, setProduto] = useState('');
  const [categoria, setCategoria] = useState('');
  const [descricao, setDescricao] = useState('');
  const [estado, setEstado] = useState('');
  const [contato, setContato] = useState('');
  const [imagem, setImagem] = useState('');
  const [imagemArquivo, setImagemArquivo] = useState(null);
  const { adicionarProduto } = useProdutos();
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (produto && categoria && descricao && estado && contato) {
      adicionarProduto({
        nome: produto,
        categoria,
        descricao,
        estado,
        contato,
        imagem
      });
      alert('Doação cadastrada com sucesso!');
      setProduto('');
      setCategoria('');
      setDescricao('');
      setEstado('');
      setContato('');
      setImagem('');
      setImagemArquivo(null);
      navigate('/home');
    }
  };

  return (
    <div className="container">
      <div style={{ marginBottom: '20px' }}>
        <Link to="/home" style={{ color: '#ff69b4', textDecoration: 'none' }}>← Voltar ao Início</Link>
      </div>
      <h2 style={{ textAlign: 'center', marginBottom: '20px', color: '#ff69b4' }}>
        Doar Produto - Além do Positivo
      </h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome do Produto:</label>
          <input
            type="text"
            value={produto}
            onChange={(e) => setProduto(e.target.value)}
            placeholder="Ex: Carrinho de bebê"
            required
          />
        </div>

        <div className="form-group">
          <label>Categoria:</label>
          <select
            value={categoria}
            onChange={(e) => setCategoria(e.target.value)}
            required
          >
            <option value="">Selecione uma categoria</option>
            <option value="roupas">Roupas</option>
            <option value="brinquedos">Brinquedos</option>
            <option value="moveis">Móveis</option>
            <option value="acessorios">Acessórios</option>
            <option value="alimentacao">Alimentação</option>
            <option value="outros">Outros</option>
          </select>
        </div>

        <div className="form-group">
          <label>Descrição:</label>
          <textarea
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            placeholder="Descreva o produto, tamanho, cor, etc."
            rows="4"
            required
          />
        </div>

        <div className="form-group">
          <label>Estado do Produto:</label>
          <select
            value={estado}
            onChange={(e) => setEstado(e.target.value)}
            required
          >
            <option value="">Selecione o estado</option>
            <option value="novo">Novo</option>
            <option value="seminovo">Semi-novo</option>
            <option value="usado">Usado (bom estado)</option>
          </select>
        </div>

        <div className="form-group">
          <label>Contato (WhatsApp):</label>
          <input
            type="tel"
            value={contato}
            onChange={(e) => setContato(e.target.value)}
            placeholder="(11) 99999-9999"
            required
          />
        </div>

        <div className="form-group">
          <label>Imagem do Produto (opcional):</label>
          <div style={{ marginBottom: '10px' }}>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const file = e.target.files[0];
                if (file) {
                  setImagemArquivo(file);
                  const reader = new FileReader();
                  reader.onload = (event) => setImagem(event.target.result);
                  reader.readAsDataURL(file);
                }
              }}
              style={{ marginBottom: '10px' }}
            />
          </div>
          <div style={{ fontSize: '14px', color: '#666', marginBottom: '10px' }}>ou</div>
          <input
            type="url"
            value={imagemArquivo ? '' : imagem}
            onChange={(e) => {
              setImagem(e.target.value);
              setImagemArquivo(null);
            }}
            placeholder="https://exemplo.com/imagem.jpg"
            disabled={imagemArquivo !== null}
          />
          {imagem && (
            <div style={{ marginTop: '10px' }}>
              <img 
                src={imagem} 
                alt="Preview" 
                style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '4px' }}
              />
            </div>
          )}
        </div>

        <button type="submit" className="btn">Cadastrar Doação</button>
      </form>

      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
        <h4 style={{ color: '#ff69b4', marginBottom: '10px' }}>Como funciona?</h4>
        <p>1. Preencha o formulário com os dados do produto</p>
        <p>2. Nossa equipe entrará em contato para combinar a retirada</p>
        <p>3. Seu produto será destinado a famílias necessitadas</p>
      </div>
    </div>
  );
}

export default Doacao;
