          <div style={{ textAlign: 'center', marginBottom: '40px' }}>
            <h1 style={{ 
              color: theme.primary, 
              fontSize: '32px', 
              fontWeight: '700',
              marginBottom: '8px',
              letterSpacing: '-0.5px'
            }}>
