import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';

function Admin() {
  const { theme, isDark, toggleTheme } = useTheme();
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ total: 0, ativos: 0, inativos: 0, admins: 0 });

  useEffect(() => {
    carregarUsuarios();
  }, []);

  const carregarUsuarios = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/usuarios');
      if (response.ok) {
        const data = await response.json();
        setUsuarios(data);
        
        // Calcular estat√≠sticas
        const total = data.length;
        const ativos = data.filter(u => u.status === 'ativo').length;
        const inativos = data.filter(u => u.status === 'inativo').length;
        const admins = data.filter(u => u.isAdmin).length;
        setStats({ total, ativos, inativos, admins });
      } else {
        alert('Erro ao carregar usu√°rios');
      }
    } catch (error) {
      alert('Erro de conex√£o com o servidor');
    } finally {
      setLoading(false);
    }
  };

  const toggleStatus = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/usuarios/${id}/status`, {
        method: 'PUT',
      });
      if (response.ok) {
        carregarUsuarios();
      } else {
        alert('Erro ao alterar status do usu√°rio');
      }
    } catch (error) {
      alert('Erro de conex√£o com o servidor');
    }
  };

  const removerUsuario = async (id) => {
    if (window.confirm('Tem certeza que deseja remover este usu√°rio?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/usuarios/${id}`, {
          method: 'DELETE',
        });
        if (response.ok) {
          carregarUsuarios();
        } else {
          alert('Erro ao remover usu√°rio');
        }
      } catch (error) {
        alert('Erro de conex√£o com o servidor');
      }
    }
  };

