                  <th style={{ padding: '15px', textAlign: 'left', color: theme.text, fontWeight: '600', fontSize: '14px' }}>üìß EMAIL</th>
                  <th style={{ padding: '15px', textAlign: 'center', color: theme.text, fontWeight: '600', fontSize: '14px' }}>üìä STATUS</th>
