                  <th style={{ padding: '15px', textAlign: 'center', color: theme.text, fontWeight: '600', fontSize: '14px' }}>‚ö° A√á√ïES</th>
                </tr>
              </thead>
              <tbody>
                {usuarios.map((usuario, index) => (
                  <tr key={usuario.id} style={{ 
                    borderBottom: `1px solid ${theme.border}`,
                    backgroundColor: index % 2 === 0 ? 'transparent' : (isDark ? 'rgba(69, 75, 96, 0.2)' : 'rgba(247, 182, 186, 0.1)')
