import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { useProdutos } from '../context/ProdutosContext';

function DetalhesProduto() {
  const { id } = useParams();
  const { produtos } = useProdutos();

  const produto = produtos.find(p => p.id === parseInt(id));

  if (!produto) {
    return (
      <div className="container">
        <div style={{ marginBottom: '20px' }}>
          <Link to="/home" style={{ color: '#ff69b4', textDecoration: 'none' }}>â Voltar ao InÃ­cio</Link>
        </div>
        <h2 style={{ textAlign: 'center', color: '#ff69b4' }}>Produto nÃ£o encontrado</h2>
      </div>
    );
  }

  const handleWhatsApp = () => {
    const mensagem = `OlÃ¡! Tenho interesse no produto: ${produto.nome}`;
    const numero = produto.contato.replace(/\D/g, '');
    window.open(`https://wa.me/55${numero}?text=${encodeURIComponent(mensagem)}`, '_blank');
  };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '20px' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ marginBottom: '20px' }}>
          <Link to="/home" style={{ color: '#ff69b4', textDecoration: 'none', fontSize: '16px' }}>â Voltar ao InÃ­cio</Link>
        </div>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'minmax(300px, 1fr) 1fr', 
          gap: '40px',
          backgroundColor: 'white',
          padding: '30px',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div>
            {produto.imagem ? (
              <img 
                src={produto.imagem} 
                alt={produto.nome}
                style={{ 
                  width: '100%', 
                  height: '400px', 
                  objectFit: 'cover', 
                  borderRadius: '8px',
                  border: '1px solid #ddd'
                }}
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/400x400?text=Sem+Imagem';
                }}
              />
            ) : (
              <div style={{
                width: '100%',
                height: '400px',
                backgroundColor: '#f0f0f0',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '8px',
                border: '1px solid #ddd',
                color: '#666'
              }}>
                Sem imagem disponÃ­vel
              </div>
            )}
          </div>

          <div>
            <h1 style={{ color: '#ff69b4', marginBottom: '30px', fontSize: '2.5rem' }}>{produto.nome}</h1>

            <div style={{ marginBottom: '20px', fontSize: '18px' }}>
              <strong>Categoria:</strong> <span style={{ textTransform: 'capitalize' }}>{produto.categoria}</span>
            </div>

            <div style={{ marginBottom: '20px', fontSize: '18px' }}>
              <strong>Estado:</strong> <span style={{ textTransform: 'capitalize' }}>{produto.estado}</span>
            </div>

            <div style={{ marginBottom: '25px' }}>
              <strong style={{ fontSize: '18px' }}>DescriÃ§Ã£o:</strong>
              <p style={{ marginTop: '10px', lineHeight: '1.6', fontSize: '16px', color: '#555' }}>{produto.descricao}</p>
            </div>

            <div style={{ marginBottom: '20px', fontSize: '18px' }}>
              <strong>Doador:</strong> {produto.doador}
            </div>

            <div style={{ marginBottom: '30px', fontSize: '18px' }}>
              <strong>Contato:</strong> {produto.contato}
            </div>

            <button 
              onClick={handleWhatsApp}
              style={{ 
                width: '100%',
                padding: '15px',
                backgroundColor: '#25D366',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '18px',
                cursor: 'pointer',
                fontWeight: 'bold',
                transition: 'background-color 0.3s'
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = '#128C7E'}
              onMouseOut={(e) => e.target.style.backgroundColor = '#25D366'}
            >
              ð¬ Entrar em Contato via WhatsApp
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DetalhesProduto;
