import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useProdutos } from '../context/ProdutosContext';

function Home({ user, setUser }) {
  const navigate = useNavigate();
  const { produtos } = useProdutos();

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  const handleContact = (produtoId) => {
    navigate(`/produto/${produtoId}`);
  };

  return (
    <div>
      <header className="header">
        <div className="nav">
          <h1>Além do Positivo</h1>
          <div>
            <span>Olá, {user.nome}!</span>
            <button className="logout-btn" onClick={handleLogout}>Sair</button>
          </div>
        </div>
        <div style={{ textAlign: 'center', padding: '10px', backgroundColor: '#f8f9fa' }}>
          <Link to="/doacao" style={{ margin: '0 15px', color: '#ff69b4', textDecoration: 'none', fontWeight: 'bold' }}>Doar Produto</Link>
          {user.isAdmin && (
            <Link to="/admin" style={{ margin: '0 15px', color: '#ff69b4', textDecoration: 'none', fontWeight: 'bold' }}>Administração</Link>
          )}
        </div>
      </header>

      <div className="products-grid">
        {produtos.map(produto => (
          <div key={produto.id} className="product-card">
            <h3>{produto.nome}</h3>
            <p><strong>Categoria:</strong> {produto.categoria}</p>
            <p><strong>Estado:</strong> {produto.estado}</p>
            <p><strong>Descrição:</strong> {produto.descricao}</p>
            <p><strong>Doador:</strong> {produto.doador}</p>
            <button 
              className="contact-btn"
              onClick={() => handleContact(produto.id)}
            >
              Entrar em Contato
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
